const cardsHistory=document.querySelector(".cards-history"),btnSearchCards=document.querySelectorAll(".search-card");function toggler(){const e=document.querySelector(".toggler"),c=document.querySelector(".menu");e.addEventListener("change",(function(){e.checked?c.classList.add("menu--active"):c.classList.remove("menu--active")}))}toggler();const modalRegistry=document.querySelector(".modal-registry");function SearchCards(){btnSearchCards.forEach(e=>{e.addEventListener("click",(function(){modalRegistry.classList.add("modal-registry--active")}))})}function delClass(){document.querySelectorAll(".btn-choose-cancel").forEach(e=>{e.addEventListener("click",(function(){modalRegistry.classList.remove("modal-registry--active")}))})}SearchCards(),delClass();